function remove_pos_database() {
localStorage.clear();
var database_template = this.odoo.session_info.db;
for (var i = 0; i < 100; i++) {
indexedDB.deleteDatabase(database_template + '_' + i);
console.log('removed db: ' + database_template + '_' + i);
}
indexedDB.deleteDatabase('POS-DB');
}
